import React, { Component } from 'react'
import axios from 'axios';
//import { LANGUAGES } from './_i18n/languages';



export default class Pokemon extends Component {

    state={
        name:'',
        pokemonIndex:'',
        imageUrl:'',
        type: [],
        decriptio:'pt-br',
        stats:{
            hp:"",
            attack:"",
            defense:"",
            speed:"",
            specialAttack:"",
            specialDefense:""
        },
        height:"",
        weight:"",
        eggGroup:"",
        abilities:"",
        genderRatioMale:"",
        genderRatioFemale:"",
        evs:"",
        hastchSteps:""

    };

    async componentDidMount(){
        const { pokemonIndex } = this.props.match.params;

        // Urls do pokemon
        const pokemonUrl = `https://pokeapi.co/api/v2/pokemon/${pokemonIndex}/`;
        const pokemonSpeciesUrl = `https://pokeapi.co/api/v2/pokemon-species/${pokemonIndex}/`;

        // pegue as informações do pokemon
        const pokemonRes = await axios.get(pokemonUrl);
        const name = pokemonRes.data.name;
        const imageUrl = pokemonRes.data.sprites.front_default;

        let {hp,attack,defense,speed,specialAttack,specialDefense} = '';
        pokemonRes.data.stats.map(stat=>{
            switch(stat.stat.name){
                case 'hp':
                    hp=stat['base_stat'];
                    break;
                 case 'attack':
                    attack=stat['base_stat'];
                    break;
                case 'defense':
                    defense=stat['base_stat'];
                     break;
                case 'speed':
                    speed=stat['base_stat'];
                    break;
                case 'special-attack':
                    specialAttack=stat['base_stat'];
                    break;
                case 'special-defense':
                    specialDefense=stat['base_stat'];
                    break;
                default:
                    break;
            }

        });
        //conversor de decimetros 
        const height = 
           Math.round((pokemonRes.data.height * 0.328084 + 0.0001) * 100) / 100;

           // conversor to lbs
        const weight =
           Math.round((pokemonRes.data.weight * 0.220462 + 0.0001) * 100) / 100;

        const type = pokemonRes.data.types.map(type => type.type.name);

        const abilities = pokemonRes.data.abilities.map(ability =>{
            return ability.ability.name
            .toLowerCase()
            .split('-')
            .map(s => s.charAt(0).toUpperCase() + s.substring(1))
            .join('');
        })

        const evs = pokemonRes.data.stats.filter(stat =>{
            if (stat.effort > 0){
                return true;
            }
                return false;
        }).map(stat => {
            return`${stat.effor} ${stat.stat.name}`
            .toLowerCase()
            .split('-')
            .map(s => s.charAt(0).toUpperCase() + s.substring(1))
            .join('');
        })
        .join(',');

        //pegar descrições do pokemon
        await axios.get(pokemonSpeciesUrl).then(res =>{
            let description = '';
            res.data.flavor_text_entries.some(flavor =>{
                if(flavor.language.name === 'pt-br'){
                    description = flavor.flavor_text;
                    return;
                }
            });

            const femaleRate = res.data['gender_rate'];
            const genderRatioFemale = 12.5 * femaleRate;
            const genderRatioMale = 12.5 * (8 - femaleRate);

            const catchRate = Math.round((100 / 255) * res.data['capture_rate']);

            const eggGroups = res.data['egg_groups']
            .map(group => {
                return group.name
                .toLowerCase()
                .split('-')
                .map(s => s.charAt(0).toUpperCase() + s.substring(1))
                .join(' ');
                }).join(',');

                const hastchSteps = 255 * (res.data["hatch_counter"] + 1);
                this.setState({
                    description,
                    genderRatioFemale,
                    genderRatioMale,
                    catchRate,
                    eggGroups,
                    hastchSteps
                })
            });

                this.setState({
                  imageUrl,
                  pokemonIndex,
                  name,
                  type,
                  state: {
                      hp,
                      attack,
                      defense,
                      speed,
                      specialAttack,
                      specialDefense
                  },
                    height,
                    weight,
                    abilities,
                    evs

                });

    }
    render() {
        return  (

         <div className = "col">
            <div className = "card">
                <div className = "card-header">
                    <div className = "row">
                        <div className = "clo-5">
        <h5>{this.state.pokemonIndex}</h5>
                        </div>
                        <div className = "col-7">
                            <div className = "float-right">
                                {this.state.type.map(type =>(
                                    <span key = {type}
                                className = "badge badge-primary badge-pill mr-1">{type}</span>
                                ))}
                            </div> 
                        </div>
                    </div>
                </div> 
            </div>
         </div>
        );
    };

}
